
public class 비교연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10, b = 5;
		
		System.out.println(a == b); //같나?
		System.out.println(a != b); //같지 않나?
		System.out.println(a > b);
		System.out.println(a <= b);
		
		// **연산의 결과값은  boolean형
		
		boolean isTrue = a==b;

	}

}
