
public class 산술연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(5+7);
		System.out.println(5-7);
		System.out.println(5/7);
		System.out.println(5/7.0);
		System.out.println(7%5); //나머지 연산 : 주로 홀/짝 구분, 배수 구분할 때 사용
		
		System.out.println("문자"+"주세요");
//		System.out.println("안녕하세요"-"안녕"); //문자 빼기는 안됨

	}

}
